{
    "compilerOptions": {
        "target": "ES6",
        "inlineSourceMap": true,
        "inlineSources": true,
        "moduleResolution": "node",
        "rootDir": "packages",
        "mapRoot": "packages",
        "noEmit": true
    },
    "exclude": [
        "node_modules",
        "dist",
        "demos",
        "tmp",
        "bower_components",
        ".vscode"
    ]
}
